func:
if (!msg.clientId || msg.clientId === 'YOUR_CLIENT_ID') {
    node.warn('Strava credentials not set. Edit the inject node.');
    return null;
}

flow.set('clientId', msg.clientId);
flow.set('clientSecret', msg.clientSecret);
flow.set('vaultPath', msg.vaultPath);
flow.set('googleMapsKey', msg.googleMapsKey);

// Seed persisted refresh token from inject on first run
const storedRefresh = flow.get('refreshToken', 'file');
if (!storedRefresh) {
    flow.set('refreshToken', msg.refreshToken, 'file');
}

const expiresAt = flow.get('expiresAt', 'file') || 0;
const now = Math.floor(Date.now() / 1000);

msg._source = 'poll';

if (now >= expiresAt - 60) {
    return [null, msg];
}

return [msg, null];

const accessToken = flow.get('accessToken', 'file');
if (!accessToken) {
    node.warn('No access token available.');
    return null;
}

let lastTs = flow.get('lastTimestamp', 'file');
if (!lastTs) {
    lastTs = Math.floor(Date.now() / 1000) - 86400;
    flow.set('lastTimestamp', lastTs, 'file');
}

msg.url = 'https://www.strava.com/api/v3/athlete/activities'
    + '?after=' + (lastTs - 86400)
    + '&per_page=200';

msg.headers = { 'Authorization': 'Bearer ' + accessToken };

return msg;

const items = msg.payload;
if (!Array.isArray(items)) {
    node.warn('Unexpected API response: ' + JSON.stringify(msg.payload));
    return null;
}

if (items.length === 0) {
    node.log('No new activities found.');
    return null;
}

let maxTs = flow.get('lastTimestamp', 'file') || 0;
for (const item of items) {
    const ts = Math.floor(new Date(item.start_date).getTime() / 1000);
    if (ts > maxTs) maxTs = ts;
}
flow.set('lastTimestamp', maxTs, 'file');

node.log('Found ' + items.length + ' new activity/activities.');

for (const item of items) {
    node.send({ payload: item });
}

return null;

if (!msg.clientId || msg.clientId === 'YOUR_CLIENT_ID') {
    node.warn('Strava credentials not set. Edit the inject node.');
    return null;
}

flow.set('clientId', msg.clientId);
flow.set('clientSecret', msg.clientSecret);
flow.set('vaultPath', msg.vaultPath);
flow.set('googleMapsKey', msg.googleMapsKey);

const storedRefresh = flow.get('refreshToken', 'file');
if (!storedRefresh) {
    flow.set('refreshToken', msg.refreshToken, 'file');
}

const expiresAt = flow.get('expiresAt', 'file') || 0;
const now = Math.floor(Date.now() / 1000);

msg._source = 'historic';

if (now >= expiresAt - 60) {
    return [null, msg];
}

return [msg, null];

const year = msg.historyYear || new Date().getFullYear();

flow.set('vaultPath', msg.vaultPath);

const after = Math.floor(Date.UTC(year, 0, 1) / 1000);
const before = Math.floor(Date.UTC(year + 1, 0, 1) / 1000);

msg.after = after;
msg.before = before;
msg.page = 1;
msg.perPage = 200;
msg.historyYear = year;

node.log('Loading activities for year ' + year);
node.status({ text: 'Loading year ' + year });
return msg;

const accessToken = flow.get('accessToken', 'file');
if (!accessToken) {
    node.warn('No access token available.');
    return null;
}

msg.url = 'https://www.strava.com/api/v3/athlete/activities'
    + '?after=' + msg.after
    + '&before=' + msg.before
    + '&page=' + msg.page
    + '&per_page=' + msg.perPage;

msg.headers = { 'Authorization': 'Bearer ' + accessToken };

return msg;

const items = msg.payload;
if (!Array.isArray(items)) {
    node.warn('Unexpected API response: ' + JSON.stringify(msg.payload));
    return [null, null, { payload: 'Error at page ' + msg.page }];
}

if (items.length === 0) {
    node.log('Historic load complete.');
    return [null, null, { payload: 'Done. No more activities.' }];
}

node.log('Processing ' + items.length + ' activities at page ' + msg.page);
node.status({ text: 'Page ' + msg.page + ', got ' + items.length });

for (const item of items) {
    node.send([{ payload: item }, null, null]);
}

if (items.length >= msg.perPage) {
    const nextMsg = {
        after: msg.after,
        before: msg.before,
        page: msg.page + 1,
        perPage: msg.perPage,
        historyYear: msg.historyYear
    };
    node.send([null, nextMsg, null]);
} else {
    node.log('Historic load complete. Last page: ' + items.length + ' activities.');
    node.send([null, null, { payload: 'Done. All activities loaded for ' + msg.historyYear }]);
}

return null;

const clientId = flow.get('clientId');
const clientSecret = flow.get('clientSecret');
const refreshToken = flow.get('refreshToken', 'file');

if (!clientId || !clientSecret || !refreshToken) {
    node.warn('Missing credentials for token refresh.');
    return null;
}

msg.url = 'https://www.strava.com/oauth/token';
msg.headers = { 'Content-Type': 'application/x-www-form-urlencoded' };
msg.payload = 'client_id=' + clientId
    + '&client_secret=' + clientSecret
    + '&refresh_token=' + refreshToken
    + '&grant_type=refresh_token';

return msg;

const body = msg.payload;
if (!body || !body.access_token) {
    node.warn('Token refresh failed: ' + JSON.stringify(body));
    return null;
}

flow.set('accessToken', body.access_token, 'file');
flow.set('expiresAt', body.expires_at, 'file');
if (body.refresh_token) {
    flow.set('refreshToken', body.refresh_token, 'file');
}

node.log('Token refreshed. Expires at ' + new Date(body.expires_at * 1000).toISOString());

return msg;

const accessToken = flow.get('accessToken', 'file');
if (!accessToken) {
    node.warn('No access token available for detail fetch.');
    return null;
}

const actId = msg.payload && msg.payload.id;
if (!actId) {
    node.warn('No activity ID in payload.');
    return null;
}

msg.url = 'https://www.strava.com/api/v3/activities/' + actId;
msg.headers = { 'Authorization': 'Bearer ' + accessToken };

return msg;

if (!msg.payload || !msg.payload.id) {
    node.warn('Detail fetch failed: ' + JSON.stringify(msg.payload));
    return null;
}

return msg;

const act = msg.payload;
if (!act || !act.start_date_local) {
    return null;
}

// Parse ISO8601 local time
const d = new Date(act.start_date_local);
const year = d.getFullYear();
const month = String(d.getMonth() + 1).padStart(2, '0');
const day = String(d.getDate()).padStart(2, '0');
const hours = String(d.getHours()).padStart(2, '0');
const minutes = String(d.getMinutes()).padStart(2, '0');

// Activity type emoji
const emojiMap = {
    'Run': 'ðŸƒ', 'Ride': 'ðŸš´', 'Swim': 'ðŸŠ',
    'Hike': 'â›°ï¸', 'Walk': 'ðŸš¶', 'Yoga': 'ðŸ§˜',
    'WeightTraining': 'ðŸ‹ï¸', 'Workout': 'ðŸ‹ï¸'
};
const type = act.type || 'Workout';
const emoji = emojiMap[type] || 'ðŸƒ';

// Distance in km
const distKm = act.distance ? (act.distance / 1000).toFixed(2) : null;

// Moving time formatted
const totalSec = act.moving_time || 0;
const hrs = Math.floor(totalSec / 3600);
const mins = Math.floor((totalSec % 3600) / 60);
const secs = totalSec % 60;
let timeStr;
if (hrs > 0) {
    timeStr = hrs + 'h ' + mins + 'm ' + secs + 's';
} else {
    timeStr = mins + 'm ' + secs + 's';
}

// Elevation
const elev = act.total_elevation_gain ? Math.round(act.total_elevation_gain) : null;

// Pace or speed
let paceOrSpeed = '';
const paceTypes = ['Run', 'Walk', 'Hike'];
if (distKm && totalSec > 0) {
    if (paceTypes.includes(type)) {
        const paceSecPerKm = totalSec / (act.distance / 1000);
        const paceMin = Math.floor(paceSecPerKm / 60);
        const paceSec = Math.round(paceSecPerKm % 60);
        paceOrSpeed = '**Pace:** ' + paceMin + ':' + String(paceSec).padStart(2, '0') + ' /km';
    } else {
        const speedKmh = ((act.distance / 1000) / (totalSec / 3600)).toFixed(1);
        paceOrSpeed = '**Speed:** ' + speedKmh + ' km/h';
    }
}

// Heart rate
let hrStr = '';
if (act.average_heartrate) {
    hrStr = '**Heart Rate:** ' + Math.round(act.average_heartrate);
    if (act.max_heartrate) {
        hrStr += ' / ' + Math.round(act.max_heartrate);
    }
    hrStr += ' bpm';
}

// Build lines
const lines = [];
lines.push('- ' + hours + ':' + minutes + ' ' + emoji + ' [' + (act.name || 'Activity') + '](https://www.strava.com/activities/' + act.id + ') : ' + type + ' #strava');

// Description (from detail endpoint)
if (act.description && act.description.trim().length > 0) {
    lines.push('\t- ' + act.description.trim().replace(/\n/g, ' '));
}

// Detail line 1: distance, time, elevation
const detail1 = [];
if (distKm) detail1.push('**Distance:** ' + distKm + ' km');
detail1.push('**Time:** ' + timeStr);
if (elev !== null) detail1.push('**Elevation:** ' + elev + ' m');
if (detail1.length > 0) {
    lines.push('\t- ' + detail1.join(' | '));
}

// Detail line 2: pace/speed, heart rate
const detail2 = [];
if (paceOrSpeed) detail2.push(paceOrSpeed);
if (hrStr) detail2.push(hrStr);
if (detail2.length > 0) {
    lines.push('\t- ' + detail2.join(' | '));
}

// Detail line 3: achievements, kudos
const detail3 = [];
if (act.achievement_count !== undefined) detail3.push('**Achievements:** ' + act.achievement_count);
if (act.kudos_count !== undefined) detail3.push('**Kudos:** ' + act.kudos_count);
if (detail3.length > 0) {
    lines.push('\t- ' + detail3.join(' | '));
}

// Map: static image if Google Maps key configured, otherwise OSM link
const googleMapsKey = flow.get('googleMapsKey');
const polyline = act.map && act.map.summary_polyline;
if (polyline && googleMapsKey && googleMapsKey !== 'YOUR_GOOGLE_MAPS_KEY') {
    const encodedPolyline = encodeURIComponent(polyline);
    lines.push('\t- ![Map](https://maps.googleapis.com/maps/api/staticmap?size=600x300&path=weight:3%7Ccolor:red%7Cenc:' + encodedPolyline + '&key=' + googleMapsKey + ')');
} else if (act.start_latlng && act.start_latlng.length === 2) {
    const lat = act.start_latlng[0].toFixed(4);
    const lng = act.start_latlng[1].toFixed(4);
    lines.push('\t- [Map](https://www.openstreetmap.org/?mlat=' + lat + '&mlon=' + lng + '#map=15/' + lat + '/' + lng + ')');
}

msg.noteupdate = lines.join('\n');
msg.activityId = String(act.id);
msg.payload = Date.now();

msg.dateStr = { year: String(year), month, day };
msg.files = { dailynotefile: year + '-' + month + '-' + day + '.md' };

return msg;

const vaultPath = flow.get('vaultPath') || '/kms/10 PMS/11 Daily Notes';

msg.filename = vaultPath
    + '/' + msg.dateStr.year
    + '/' + msg.dateStr.month
    + '/' + msg.files.dailynotefile;

return msg;

const activity = msg.noteupdate;
let content = '';

if (typeof msg.payload === 'string' && msg.payload.length > 0) {
    content = msg.payload;
} else {
    content = '';
}

// Duplicate check: skip if this activity ID is already in the file
if (msg.activityId && content.indexOf(msg.activityId) !== -1) {
    node.log('Activity ' + msg.activityId + ' already exists, skipping.');
    return null;
}

const notesHeading = '## Notes';
const notesIdx = content.indexOf(notesHeading);

if (notesIdx !== -1) {
    const afterNotes = content.substring(notesIdx + notesHeading.length);
    const nextHeadingMatch = afterNotes.match(/\n## [^#]/);

    if (nextHeadingMatch) {
        const insertPos = notesIdx + notesHeading.length + nextHeadingMatch.index;
        msg.payload = content.substring(0, insertPos) + '\n' + activity + content.substring(insertPos);
    } else {
        msg.payload = content.trimEnd() + '\n' + activity + '\n';
    }
} else {
    msg.payload = content.trimEnd() + '\n\n## Notes\n' + activity + '\n';
}

return msg;
